import React from 'react';
import styled from 'styled-components';

import styles from './styles.css';

const Wrapper = styled.div`
  box-sizing: border-box;
  width: 100%;
  padding: 20px;
  margin-top: 20px;
  background-color: white;
  border-radius: 5px;
  box-shadow: 0px 2px 8px rgba(0, 0, 0, 0.1);
  display: flex;
  flex-flow: column nowrap;
`;

const RowStyled = styled.div`
  width: 100%;
  height: 40px;
  display: flex;
  flex-flow: row wrap;
`;

const Row = props => {
  return (
    <RowStyled>
      <Cell>
        <header>{props.origin} - {props.destination}</header>
        <main>{props.date.slice(0, 10)}</main>
      </Cell>
      <Cell>
        <header>В пути</header>
        <main>{props.duration}</main>
      </Cell>
      <Cell>
        <header>{props.stops.length} Пересадок</header>
        <main>{props.stops.join(',')}</main>
      </Cell>
    </RowStyled>
  );
}

const Cell = styled.div`
  flex: 1 1 0;
  display: flex;
  flex-flow: column nowrap;
  align-items: flex-start;

  header {
    font-size: 12px;
    line-height: 18px;
    text-transform: uppercase;
    color: #A0B0B9;
  }

  main {
    font-size: 14px;
    line-height: 21px;
    font-weight: 600;
    color: #4A4A4A;
  }
`;

const Header = () => <Row className={styles.header}/>;

export const Item = props => {
  return (
    <Wrapper>
      <Header>
      </Header>
      <Row props={props.segments[0]} />
      <Row>
      </Row>
    </Wrapper>
  );
}
