import React, {useState} from 'react';
import styled from 'styled-components';

import { Button } from './Button';

const Wrapper = styled.div`
  width: 100%;
  height: 50px
  border: 1px solid #E5E5E5;
  border-radius: 5px;
`;

export const Toggler = props => {
  const [leftActive, setLeftActive] = useState(true);

  const handleLeftClick = e => {
    e.preventDefautlt();
    setLeftActive(true);
  }

  const handleRightClick = e => {
    e.preventDefautlt();
    setLeftActive(false);
  }

  return (
    <Wrapper>
      <Button active={leftActive} onClick={handleLeftClick}>
        Самый дешёвый
      </Button>
      <Button active={!leftActive} onClick={handleRightClick}>
        Самый быстрый
      </Button>
    </Wrapper>
  );
}
