import { all, call, put, takeEvery, takeLatest, delay } from 'redux-saga/effects';

import { join } from './actions/tickets';

function* updateApi() {
  // while (true) {
    try {
      const { searchId } = yield call(() => {
        return fetch('https://front-test.beta.aviasales.ru/search').then(res => res.json())
      })
      console.log(searchId);
      const data = yield call(() => {
        return fetch(`https://front-test.beta.aviasales.ru/tickets?searchId=${searchId}`).then(res => res.json())
        }
      );
      return data;
    } catch(error) {
      yield delay(2000);
    }
  // }
}

function* updateResource() {

  do {
    var apiResponse = yield call(updateApi);
    console.log(apiResponse.tickets);
    yield put(join(apiResponse.tickets));
  } while (!apiResponse.stop);
}

export function* watchUpdateResource() {
  yield takeLatest('UPDATE_START', updateResource);
}


export function* rootSaga() {
  yield all([
    updateResource()
  ])
}
