import { all, call, put, takeEvery } from 'redux-saga/effects';
import { useDispatch} from 'react-redux';

function* poll(action) {
  const params = { ...action.params };
  const stats = {
      isPolling: false,
      fetching: false,
  };

  while(true) {
      // Start polling
      stats.isPolling = true;

      try {
          // Make the API call
          stats.fetching = true;
          params.onStatsChange(stats);
          const response = yield call(params.asyncFetch);

          stats.fetching = false;
          stats.nextPollEta = params.delay;
          const shouldContinue = params.callback(response, stats);

          // API call was successful
          if (shouldContinue) {
              stats.retries = 0;
              stats.lastResponseStatus = 'success';
              params.onStatsChange(stats);
          } else {
              params.onStatsChange(stats);
              throw new Error('Error while fetching data.');
          }

          for (let i = 1; i <= params.delay; ++i) {
              yield call(delay, 1000);
              stats.nextPollEta = params.delay - i;
              params.onStatsChange(stats);
          }
      } catch (e) {

      }
  }
}

export function* helloSaga() {
  console.log('Hello Sagas!');

  let x = new XMLHttpRequest();

  const get = () => {
    x.open("GET", "https://front-test.beta.aviasales.ru/search", true);
    x.onload = () => {
      alert(x.responseText);
      useDispatch({ type: 'DATA_FETCHED' });
    }
    x.send(null);
  }

  yield call(get);
  // yield put({ type: 'DATA_FETCHED' });
}

export function* watchSaga() {
  yield takeEvery('DATA_FETCHED', helloSaga)
}

export function* rootSaga() {
  yield all([
    helloSaga(),
    watchSaga()
  ])
}
