import React from 'react';
import styled from 'styled-components';

import { Box } from './Box';

const Wrapper = styled.div`
  padding: 10px 0;
  cursor: pointer;
  display: flex;
  justify-content: start;
  align-items: center;
  font-size: 13px;
  ling-height: 20px;
  background-color: ${props => props.hover} ? 'white' : '#F1FCFF';

  &:hover {
    background: #F1FCFF;
  }
`;

export const Option = props => {
  return (
    <Wrapper>
      <Box active={props.active}/>
      {props.value}
    </Wrapper>
  );
}
