import { all, call, put, takeEvery, takeLatest, delay } from 'redux-saga/effects';

function* updateApi() {
  while (true) {
    try {
      const data = yield call(() => {
        return fetch('https://front-test.beta.aviasales.ru/tickets?searchId=5bsts').then(res => res.json())
        }
      );
      return data;
    } catch(error) {
      yield delay(2000);
    }
  }
}

function* updateResource() {
  const apiResponse = yield call(updateApi, data);
  if (apiResponse.stop){
    yield put({
      type: 'UPDATE_SUCCESS',
      payload: apiResponse.tickets,
    });
  } else {
    yield put({
      type: 'UPDATE_SUCCESS',
      payload: apiResponse.tickets,
    });
  }
}

export function* watchUpdateResource() {
  yield takeLatest('UPDATE_START', updateResource);
}


export function* rootSaga() {
  yield all([
    watchUpdateResource()
  ])
}
