import React from 'react';
import styled from 'styled-components';

import { useDispatch, useSelector } from 'react-redux';

import { toggle } from '../../actions/filter';

import { Option } from './Option'

const Wrapper = styled.div`
  box-sizing: content-box;
  background-color: white;
  width: 230px;
  // max-height: 150px;
  padding: 10px 0;
  line-height: 12px;
  border-radius: 5px;
  box-shadow: 0px 2px 8px rgba(0, 0, 0, 0.1);
  display: flex;
  flex-direction: column;
  font-size: 12px;
  text-transform: uppercase;
`;

const TextWrapper = styled.div`
  padding: 10px 20px;
`;

export const Filter = props => {

  const dispatch = useDispatch();
  const filter = useSelector(state => state.filter);

  const handleClick = flag => () => dispatch(toggle(flag));

  return (
    <Wrapper>
      <TextWrapper>
        количество пересадок
      </TextWrapper >
      <Option value='Все' active={filter.all} />
      <Option value='Без пересадок' active={filter.transferNo} handleClick={handleClick}/>
      <Option value='1 пересадка' active={filter.transferOne} />
      <Option value='2 пересадки' active={filter.transferTwo} />
      <Option value='3 пересадки' active={filter.tranferThree} />
    </Wrapper>
  );
}
