import { all, call, put, takeEvery, takeLatest, delay } from 'redux-saga/effects';

function* updateApi() {
  // while (true) {
    try {
      const data = yield call(() => {
        return 
          // fetch('https://front-test.beta.aviasales.ru/search').then(responce => responce.json()).then( res => 
          // fetch(`https://front-test.beta.aviasales.ru/tickets?searchId=${res.searchId}`).then(res => res.json()))
        }
      );
      return data;
    } catch(error) {
      yield delay(2000);
    }
  // }
}

function* updateResource() {
  // do {
    var apiResponse = yield call(updateApi);
    yield put({
      type: 'JOIN',
      payload: apiResponse.tickets,
    });
  // } while (!apiResponse.stop);
}

export function* watchUpdateResource() {
  yield takeLatest('UPDATE_START', updateResource);
}


export function* rootSaga() {
  yield all([
    updateResource()
  ])
}
